package day2;

public class assign {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 7;
		System.out.println(x);
		
		x += 3;
		System.out.println(x);
		x -= 3;
		System.out.println(x);
		x *= 3;
		System.out.println(x);
		x /= 3;
		System.out.println(x);
		x %= 3;
		System.out.println(x);
		x = 10;
		x &= 3;
		System.out.println(x);
		x |= 3;
		System.out.println(x);
		x = 10;
		System.out.println(x);
		x ^= 3;
		System.out.println(x);
		x >>= 3;
		System.out.println(x);
		x <<= 3;
		System.out.println(x);


	}

}
